module.exports = {
encrypt: function(message, key) {
var alphabet = ['a','b','c','d','e','f','g','h','i','j','k','l','m','n','o','p','q','r','s','t','u','v','w','x','y','z'];
var cipherText  = '';
var char;
var index;
var cipherIndex;
message = message.toLowerCase();
key = parseInt(key);


for(var i = 0; i < message.length; i++) {
  char = message.charAt(i);
  index = alphabet.indexOf(char);
  cipherIndex = index + key;

  if(index !== -1) {

    if(cipherIndex >= 26) {
      cipherIndex =- 26;
    }

    cipherText += alphabet[cipherIndex];

  }else {
    cipherText === " " ? cipherText += ' ' : cipherText += char;
  }
}
return cipherText;
},
decrypt: function(message, key) {
  var alphabet = ['a','b','c','d','e','f','g','h','i','j','k','l','m','n','o','p','q','r','s','t','u','v','w','x','y','z'];
  var plainText  = '';
  var char;
  var index;
  var cipherIndex;
  message = message.toLowerCase();
  key = parseInt(key);


  for(var i = 0; i < message.length; i++) {
    char = message.charAt(i);
    index = alphabet.indexOf(char);
    cipherIndex = index - key;

    if(index !== -1) {

      if(cipherIndex >= 26) {
        cipherIndex =- 26;
      }

      plainText += alphabet[cipherIndex];

    }else {
      plainText === " " ? plainText += ' ' : plainText += char;
    }
  }
  return plainText;
  }
};
